  -webkit-animation: blink .75s linear infinite;
  -moz-animation: blink .75s linear infinite;
  -ms-animation: blink .75s linear infinite;
  -o-animation: blink .75s linear infinite;
  animation: blink .75s linear infinite;
}

ul {
  list-style: none;
  margin-left: 30px;
}
ul li {
  margin-top: 3px;
}

